import{_ as e,c as t,o as r,a as o}from"./app.447407fd.js";const m=JSON.parse('{"title":"Resume","description":"","frontmatter":{},"headers":[{"level":2,"title":"Education details","slug":"education-details"},{"level":2,"title":"Work Experience","slug":"work-experience"},{"level":2,"title":"Academic Projects","slug":"academic-projects"},{"level":2,"title":"Other Work","slug":"other-work"},{"level":2,"title":"Technical Skills","slug":"technical-skills"}],"relativePath":"resume.md","lastUpdated":null}'),a={name:"resume.md"},n=o('<h1 id="resume" tabindex="-1">Resume <a class="header-anchor" href="#resume" aria-hidden="true">#</a></h1><p><a href="https://github.com/dhruvdh" target="_blank" rel="noreferrer">Please also see my github.</a></p><h2 id="education-details" tabindex="-1">Education details <a class="header-anchor" href="#education-details" aria-hidden="true">#</a></h2><table><thead><tr><th>Year</th><th>Degree</th><th>Institution</th></tr></thead><tbody><tr><td>2018-2019</td><td>Master&#39;s of Science in Computer Science</td><td>University of North Carolina, Charlotte</td></tr><tr><td>2014-2018</td><td>Bachelors of Engineering in Information Technology</td><td>Mumbai University</td></tr></tbody></table><h2 id="work-experience" tabindex="-1">Work Experience <a class="header-anchor" href="#work-experience" aria-hidden="true">#</a></h2><ul><li><p><strong>Adjunct Lecturer at UNC, Charlotte</strong><br><em>January 2020 - Present</em></p><p>Currently teaching ITSC 1212 Introduction to Computer Science 1, ITSC 2214 Data Structures, and ITCS 3134 - Digital Image Processing at the College of Computing and Informatics at UNC, Charlotte. Would have taught 21 sections by the Fall of 2022.</p></li><li><p><strong>Machine Learning Consultant at Powerhouse Consulting</strong><br><em>February 2020 - April 2020</em></p><p>Helped write two funding proposals, an SBIR from the Department of Education, and another for NSF on using a reinforcement learning-based intelligent aide for students with Autism spectrum disorder (ASD).</p></li><li><p><strong>Tutor at UNC, Charlotte</strong><br><em>January 2020 - May 2020</em></p><p>Tutored for ITCS 3181 - Introduction to Computer Architecture for the Spring 2020 semester at the College of Computing and Informatics at UNC, Charlotte.</p></li><li><p><strong>Lowe&#39;s Innovation Labs</strong><br><em>August 2018 - December 2018</em></p><p>Collaborated with Lowe&#39;s Innovation Labs for a single project of developing an MVP for a Visual Search system meant to identify products within Lowe&#39;s massive product catalog from images. <em>(Not an internship)</em></p></li><li><p><strong>General Secretary of Students&#39; Council</strong><br><em>August 2016 - August 2017</em></p><p>Served as the General Secretary of the St. Francis Institute of Technology Students&#39; Council (SFITSC) from 2016-2017.</p></li></ul><h2 id="academic-projects" tabindex="-1">Academic Projects <a class="header-anchor" href="#academic-projects" aria-hidden="true">#</a></h2><ul><li><p><strong>Solution for SemEval Task 3: EmoContext</strong><br><em>Natural Language Processing</em></p><p>Implemented a moderately successful solution (ranked 44th/241 as of 12/13/2018) for SemEval 2019 Task 3: EmoContext: Contextual Emotion Detection in Text., using a non-traditional Bi-LSTM architecture that used a combination of FastText and some Custom word embeddings, that performed as it did without requiring any significant preprocessing</p><p><strong>Project Link</strong>: <a href="https://github.com/DhruvDh/emocontext" target="_blank" rel="noreferrer">https://github.com/DhruvDh/emocontext</a></p></li><li><p><strong>Semi-supervised Natural Language Understanding</strong><br><em>Natural Language Processing</em></p><p>Used generative language models to solve the Natural language Understanding task of user intent identification.</p><p><strong>Project Link</strong>: <a href="https://github.com/DhruvDh/semisupervised_nlu" target="_blank" rel="noreferrer">https://github.com/DhruvDh/semisupervised_nlu</a></p></li><li><p><strong>Visual Search Prototype for Lowe\u2019s</strong><br><em>Computer Vision</em></p><p>Collaborated with Lowe\u2019s Innovation Labs to build a visual search system, that given an image, identifies the product within it, using a very small, computationally efficient Convolutional Neural Network. Was trained to detect products across the category of Decorative Light Bulbs.</p><p><strong>MVP Demo</strong>: <a href="https://youtu.be/WmK4QiC-7Ag" target="_blank" rel="noreferrer">https://youtu.be/WmK4QiC-7Ag</a></p></li><li><p><strong>Robotic Arm Manipulation using Quaternion Neural Networks</strong><br><em>Intelligent Robotics</em></p><p>Trained a Quaternion Neural Network to predict what positions the links in a robotic arm should be in for the end-effector to be placed in the desired position. Effectively did the same job as an inverse kinematics solver but with better computational efficiency.</p><p><strong>Project Link</strong>: <a href="https://dhruvdh.github.io/intelligent_robotics_project/" target="_blank" rel="noreferrer">https://dhruvdh.github.io/intelligent_robotics_project/</a></p></li><li><p><strong>Speech-To-Text</strong><br><em>Web Application</em></p><p>A progressive web application that generates transcripts from audios of recorded discussions, meetings, etc., and then uses NLP to perform a rudimentary analysis on them. Used - Vue.js, Node.js, and the Google Cloud Platform.</p><p><strong>Project Demo</strong>: <a href="https://youtu.be/KgCdh2ru6jg" target="_blank" rel="noreferrer">https://youtu.be/KgCdh2ru6jg</a></p></li><li><p><strong>Intelligent to-do list</strong><br><em>Web Application</em></p><p>An intelligent to-do list web application for students that could &quot;understand&quot; the intention behind a to-do list item and then proceed to classify to-do items and organize them automatically. It is easier to show than to explain, please refer to the demo</p><p><strong>Project Demo</strong>: <a href="https://youtu.be/FLrzDlr9oZM" target="_blank" rel="noreferrer">https://youtu.be/FLrzDlr9oZM</a></p></li><li><p><strong>Efficient Implementation of the N-Body problem on CPUs and GPUs</strong><br><em>Web Application</em></p><p>Implementation of the N-body problem that is highly optimized for Intel\u2019s Haswell micro-architecture, written in Rust. The GPU implementation leverages CUDA and is written using the Futhark Parallel Programming language.</p></li><li><p><strong>Intent Recognition from Textual User Utterances</strong><br><em>Natural Language Processing</em></p><p>Explored the feasibility of using a hierarchy of classifiers to classify the intent of a user, from textual utterances. Using the crowd-sourced Snips NLU dataset of 13,784 user utterances, the approach was found to have an overall accuracy of 95.16% while only using rudimentary text classification techniques such as Logistic Regression</p><p><strong>Project Link</strong>: <a href="https://github.com/DhruvDh/ItForSociety2018" target="_blank" rel="noreferrer">https://github.com/DhruvDh/ItForSociety2018</a></p></li></ul><h2 id="other-work" tabindex="-1">Other Work <a class="header-anchor" href="#other-work" aria-hidden="true">#</a></h2><ul><li><p><strong>umm:</strong> A java build tool for novices, also a scriptable autograder.<br><strong>Project Link:</strong> <a href="https://github.com/DhruvDh/umm" target="_blank" rel="noreferrer">https://github.com/DhruvDh/umm</a></p></li><li><p><strong>dip_app:</strong> A course website to demonstrate and give student feedback on Digital Image Processing assignments<br><strong>Project Link:</strong> <a href="https://dhruvdh.github.io/dip_app/" target="_blank" rel="noreferrer">https://dhruvdh.github.io/dip_app/</a></p></li></ul><h2 id="technical-skills" tabindex="-1">Technical Skills <a class="header-anchor" href="#technical-skills" aria-hidden="true">#</a></h2><ul><li><p><strong>Programming Languages:</strong><br><strong>Proficient in</strong>: Rust, Python, JavaScript, Bash/Shell, Java, Go.<br><strong>Can also work with</strong>: C, C++, C#, TypeScript</p></li><li><p><strong>Frameworks and Tools:</strong><br><strong>Machine Learning:</strong> PyTorch, Pandas, NumPy, Numba, libtorch, TVM, Tensorflow, StreamLit.<br><strong>Web Frameworks:</strong> Express.js, actix-web, <a href="http://rocket.rs" target="_blank" rel="noreferrer">rocket.rs</a>, Vue, React</p></li><li><p><strong>Other:</strong><br> WebAssembly, git, webpack, OpenMP, CUDA, Docker.</p></li></ul>',12),i=[n];function s(l,h,u,c,p,d){return r(),t("div",null,i)}const f=e(a,[["render",s]]);export{m as __pageData,f as default};
